      await deleteOne(chat.account._id);
    }
    if (e.errorMessage === 'INVITE_HASH_EXPIRED') {
      console.log(e.errorMessage);
      await deleteOne(chat.account._id);
    }
    if (e.errorMessage === 'FLOOD') {
      console.log(e.errorMessage);
      if (e.seconds > 300) {
        await unlinkFromAccount(chat._id);
        chat.account = null;
      }
    }
  }

